/*
 * Object vs Map Perfomance Test
 */
function timelog(fn, n) {
  console.time(fn.name);
  fn(n);
  console.timeEnd(fn.name);
}

console.log('==== ADD DELETE ====')

// Add, Delete (key-value)
function objAD(n) {
  const obj = {};
  for (let i = 0; i < n; i += 1) {
    obj[`a${i}`] = 1;
    delete obj[`a${i}`];
  }
}

function mapAD(n) {
  const map = new Map();
  for (let i = 0; i < n; i += 1) {
    map.set(`a${i}`, 1);
    map.delete(`a${i}`);
  }
}

function objAD_rand_key(n) {
  const obj = {};
  for (let i = 0; i < n; i += 1) {
    const key = Math.random().toFixed(3);
    obj[key] = 1;
    delete obj[key];
  }
}

function mapAD_rand_key(n) {
  const map = new Map();
  for (let i = 0; i < n; i += 1) {
    const key = Math.random().toFixed(3);
    map.set(key, 1);
    map.delete(key);
  }
}

function objAD_rand_key_ds(n) {
  const obj = {};
  const keys = [];
  for (let i = 0; i < n; i += 1) {
    const key = Math.random().toFixed(3);
    obj[key] = 1;
    keys.push(key);
  }

  keys.sort();
  keys.forEach((key) => delete obj[key])
}

function mapAD_rand_key_ds(n) {
  const map = new Map();
  const keys = [];

  for (let i = 0; i < n; i += 1) {
    const key = Math.random().toFixed(3);
    map.set(key, 1);
    keys.push(key);
  }
  keys.sort();
  keys.forEach((key) => map.delete(key));
}

function objA_rand_key(n) {
  const obj = {};
  for (let i = 0; i < n; i += 1) {
    const key = Math.random().toFixed(3);
    obj[key] = 1;
  }
}

function mapA_rand_key(n) {
  const map = new Map();
  for (let i = 0; i < n; i += 1) {
    const key = Math.random().toFixed(3);
    map.set(key, 1);
  }
}


