console.log('\n==== GET SET ====');
// Get, Set (key-value)

function mapGS(s) {
  const map = new Map();

  for (let i = 0; i < s.length; i += 1) {
    const char = s[i];
    const c = map.get(char);
    if (c) {
      map.set(char, c + 1);
    } else {
      map.set(char, 1);
    }
  }
}

function objGS(s) {
  const obj = {};

  for (let i = 0; i < s.length; i += 1) {
    const char = s[i];
    const c = obj[char];
    if (c) {
      obj[char] = c + 1;
    } else {
      obj[char] = 1;
    }
  }
}

